kod = ''

binary_bytes = bytes(int(kod[i:i+8], 2) for i in range(0, len(kod), 8))

# Декодирование с использованием кодировки KOI8-R
decoded_koi8_r = binary_bytes.decode('koi8_r')

# Декодирование с использованием кодировки CP866
decoded_cp866 = binary_bytes.decode('cp866')

# Декодирование с использованием кодировки CP1251
decoded_cp1251 = binary_bytes.decode('cp1251')

print("Декодировано с использованием KOI8-R:")
print(decoded_koi8_r)

print("\nДекодировано с использованием CP866:")
print(decoded_cp866)

print("\nДекодировано с использованием CP1251:")
print(decoded_cp1251)



# Таблица кодировки МТК-2
mtk2_table = {
    '00011': 'А', '11001': 'Б', '01110': 'Ц', '01001': 'Д',
    '00001': 'Е', '01101': 'Ф', '11010': 'Г', '10100': 'Х',
    '00110': 'И', '01011': 'Й', '01111': 'К', '10010': 'Л',
    '11100': 'М', '01100': 'Н', '11000': 'О', '10110': 'П',
    '10111': 'Я', '01010': 'Р', '00101': 'С', '10000': 'Т',
    '00111': 'У', '11110': 'Ж', '10011': 'В', '11101': 'Ь',
    '10101': 'Ы', '10001': 'З', '00000': '. ', '11011': '1 ',
    '00100': '  ', '11111': '2 ', '00010': '3 ', '01000': '4 '
}
kod1 = kod[:-1]
kod2 = kod1[:-1]
kod3 = kod2[:-1]
kod4 = kod3[:-1]
# Преобразование строки в список байтов
bytes_list = [kod4[i:i+5] for i in range(0, len(kod4), 5)]

# Декодирование с использованием таблицы символов МТК-2
decoded_text = ''.join(mtk2_table[byte] for byte in bytes_list)

print(decoded_text)

